    /*舰团js*/
    fleetCmDistrict : function(){
        var pattern = new RegExp("[~'!@#$%^&*()-+_=:]");
        var headline = $.trim($('#headline').val());
        var districtS = $('#_js_district').data('district-msg').split(',')[1];
        var shipName = $.trim($('#shipName').val());
        var shipId = $.trim($('#shipId').val());
        var qqId = $.trim($('#qqId').val());
        var detMsg = $.trim($('#detMsg').val());
        var imgPic = $('#_js_img').attr('src');
        if(!!headline){
            if(pattern.test(headline)){
                alert('标题非法输入');
                return
            }
        }else{
            alert('创建标题不能为空');
            return
        }
        if(!!shipName){
            if(pattern.test(shipName)){
                alert('舰团名称非法输入');
                return
            }
        }else{
            alert('舰团名称不能为空');
            return                                
        }
        if(!!shipId){
            if(pattern.test(shipId)){
                alert('舰团ID非法输入');
                return
            }
            if(!shipId.match(/[0-9]{4,}/g)){
                alert('舰团ID非法输入');
                return  
            }
        }else{
            alert('舰团ID不能为空');
            return 
        }
        if(!!qqId){
            if(pattern.test(qqId)){
                alert('QQ群号非法输入');
                return
            }
            if(!qqId.match(/[0-9]{4,}/g)){
                alert('QQ群号非法输入');
                return  
            }
        }else{
            alert('QQ群号不能为空');
            return 
        }
        if(!!detMsg){
            if(pattern.test(detMsg)){
                alert('详情内容非法输入');
                return
            }
        }else{
            alert('详情内容不能为空');
            return 
        }
        return {
            username : '我就不告诉你', // 用户名
            portrait : '../img/yuanliang@1x.png', // 头像 需要后端返回
            temporary : Date.now(),
            headline : headline,
            districtS : districtS,
            shipName : shipName,
            shipId : shipId,
            qqId : qqId,
            detMsg : detMsg,
            imgPic : imgPic,
        }
    },

